        function getOrderedCountryList(array){
            var arrayTemp= [];
            for(var i = 0; i < array.length; i++){
                for(var j = 0; j < array.length; j++){
                    if(array[i][0] == array[j][0] && thereIsCountry(arrayTemp, array[j][0]) == 0){
