                for(var j = 0; j < array.length; j++){
                    if(array[i][3] == array[j][3] && thereIsCountry(arrayTemp, array[j][3]) == 0){
                        arrayTemp[arrayTemp.length] = array[j][3];
                    }
                }
            }
            arrayTemp.sort(sortByDestination);
            return arrayTemp;
        }
        //Ordenar array por nombre de destino
        function sortByDestination(a, b){
            var aDestination = a[3].toLowerCase();
            var bDestination = b[3].toLowerCase();

            return ((aDestination < bDestination) ? -1 : ((aDestination > bDestination) ? 1 : 0));
        }
        //Ordenar array por nombre de pais
        function sortByCountry(a, b){
            var aCountry = a[0].toLowerCase();
            var bCountry = b[0].toLowerCase();

            return ((aCountry < bCountry) ? -1 : ((aCountry > bCountry) ? 1 : 0));
        }
